package com.sira.learning.javalearningdec22.jyothi;

public class methods {
	public void add()
	{
		int x=10;
		int y=20;
		int z=x+y;
		System.out.println(z);
	}
	public void sub()
	{
		int x=10;
		int y=20;
		int z=x-y;
		System.out.println(z);
	}
	public void mul()
	{
		int x=10;
		int y=20;
		int z=x*y;
		System.out.println(z);
	}
	public void div()
	{
		int x=10;
		int y=20;
		int z=x/y;
		System.out.println(z);
	}
	
	
	
	//method without retuen and parameter

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          methods ad=new methods();
          ad.add();
          ad.sub();
          ad.mul();
          ad.div();
		
		
	}

}
